mapper.select:
SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM board
		WHERE board_idx IN (
			SELECT recodenum FROM likes
			WHERE tablename = 'board'
			GROUP BY recodenum
			HAVING COUNT(*) >= 3
		)
		ORDER BY board_idx DESC
	) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM board WHERE writer_ref=#{param1}
		ORDER BY board_idx DESC
	) Tb
)
WHERE rNum>=#{param2} AND rNum<=#{param3}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM board
		WHERE board_idx IN (
			SELECT board_ref 
	        FROM comments
	        WHERE writer_ref=#{param1}
	        GROUP BY board_ref
		)
		ORDER BY board_idx DESC
	) Tb
)
WHERE rNum>=#{param2} AND rNum<=#{param3}

	SELECT * FROM (
        SELECT Tb.*, rownum rNum FROM (
            SELECT b.*
            FROM board b
            LEFT JOIN comments c ON b.board_idx = c.board_ref
            WHERE c.board_ref IS NULL AND b.boardname = 'qnaboard'
            ORDER BY b.board_idx DESC
        ) Tb
    )
    WHERE rNum >= #{start} AND rNum <= #{end}
