mapper.select:
SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM api 
		WHERE api_idx IN (
			SELECT recodenum
			FROM likes
			WHERE tablename='hospital'
			AND member_ref=#{param1}
		)
		ORDER BY api_idx DESC
	) Tb
)
WHERE rNum>=#{param2} AND rNum<=#{param3}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM doctor 
		WHERE doc_idx IN (
			SELECT recodenum
			FROM likes
			WHERE tablename='doctor'
			AND member_ref=#{param1}
		)
		ORDER BY doc_idx DESC
	) Tb
)
WHERE rNum>=#{param2} AND rNum<=#{param3}
